mapper.select:
select 

from bbs_product where is_del=1


          and name like concat('%',#{name},'%')


          and brand_id = #{brandId}


          and is_show = #{isShow}

     limit #{fromLine},#{size}

select 
count(id)
from bbs_product


          name like concat('%',#{name},'%')


          and brand_id = #{brandId}


          and is_show = #{isShow}

mapper.update:
update bbs_product
set is_del=0
where id = #{id,jdbcType=BIGINT}
